import {
  IonContent,
  IonRow,
  IonGrid,
  IonCol,
  IonCard,
  IonItem,
  IonAvatar,
  IonLabel,
  IonButton
} from '@ionic/react';


//=================================================================
//          STATELESS FUNCTIONAL COMPONENT
//=================================================================
const Users = ({users}) => {

      return (
      <>
         <IonContent>
          <IonGrid Fixed>
            <IonRow>
                {users.map(user => (
                  <IonCol  size-lg="3" size-md="4" size-sm="6" size-xs="12" key={user.id}>
                      <IonCard >
                        <IonItem>
                            <IonAvatar slot='start'>
                            <img src={process.env.PUBLIC_URL + "avatar.png"} />
                            </IonAvatar >
                            <IonLabel>
                              <h1>{user.name}</h1>
                              <IonButton color="dark">more..</IonButton>
                            </IonLabel>
                          </IonItem>
                      </IonCard>



                  </IonCol>
                ))}

            </IonRow>
          </IonGrid>
       </IonContent>
      </>
     );


}

