import {
  IonContent,
  IonRow,
  IonGrid,
  IonCol,
  IonCard,
  IonItem,
  IonAvatar,
  IonLabel,
  IonButton
} from '@ionic/react';


//=================================================================
//          STATELESS FUNCTIONAL COMPONENT
//=================================================================
const Users = ({users}) => {

      return (
      <>
         <IonContent>
          <IonGrid Fixed>
            <IonRow>
                {users.map(user => (
                  <IonCol  size-lg="3" size-md="4" size-sm="6" size-xs="12" key={user.id}>
                      <IonCard >
                        <IonItem>
                            <IonAvatar slot='start'>
                            <img src="https://cdn.pixabay.com/photo/2016/08/08/09/17/avatar-1577909_960_720.png" />
                            </IonAvatar >
                            <IonLabel>
                              <h1>{user.name}</h1>
                              <IonButton color="dark">more..</IonButton>
                            </IonLabel>
                          </IonItem>
                      </IonCard>



                  </IonCol>
                ))}

            </IonRow>
          </IonGrid>
       </IonContent>
      </>
     );


}

