// import 'bootstrap/dist/css/bootstrap.css';
import '@ionic/core/css/core.css';
import '@ionic/core/css/ionic.bundle.css';



import {
  IonContent,
  IonRow,
  IonGrid,
  IonCol,
} from '@ionic/react';


//=================================================================
//          STATELESS FUNCTIONAL COMPONENT
//=================================================================
const Users = ({users}) => {

      return (
      <>
         <IonContent>
          <IonGrid Fixed>
            <IonRow>
                {users.map(user => (
                  <IonCol  size-lg="3" size-md="4" size-sm="6" size-xs="12" key={user.id}>
                      <h1>{user.name}</h1>
                  </IonCol>
                ))}

            </IonRow>
          </IonGrid>
       </IonContent>
      </>
     );


}

